import React, { Component } from 'react';
import { Mutation } from 'react-apollo'
import Form from './styles/Form'
import gql from 'graphql-tag'
import Router from 'next/router'
import formatMoney from '../lib/formatMoney'
import Error from './ErrorMessage'

const CREATE_ITEM_MUATION = gql`
  mutation CREATE_ITEM_MUTATION(
    $title: String!
    $description: String!
    $price: Int!
    $image: String
    $largeImage: String
  ) {
    createItem(
      title: $title
      description: $description
      price: $price
      image: $image
      largeImage: $largeImage
    ) {
      id
    }
  }
`

class CreateItem extends Component {

  state = {
    title: 'Cool shoes',
    description: 'I love the convers',
    image: 'dog.jpg',
    largeImage: 'large-dog.jpg',
    price: 10,
  }

  handleChange = (e) => {
    const { name, type, value } = e.target
    const val = type === 'number' ? parseFloat(value) : value
    this.setState({ [name]: val })
  }

  uploadFile = async e => {
    console.log('Uploading file...')
    const files = e.target.files
    const data = new FormData()
    data.append('file', files[0])
    data.append('upload_preset', 'sickfits')

    const res = await fetch('http://api.cloudinary.com/v1_1/yo-mama/image/upload',
    {
      method: 'POST',
      body: data
    })
    const file = await res.json()
    console.log(file)
    this.setState({
      image: file.secure_url,
      largeImage: file.eager[0].secure_url
    })
  }

  render() {
    return (
    <Mutation mutation={CREATE_ITEM_MUATION} variables={this.state}>
      {(createItem, { loading, error }) => (

        <Form onSubmit={async e => {
          e.preventDefault()
          const res = await createItem()
          console.log(res)
          Router.push({
            pathname: '/item',
            query: { id: res.data.createItem.id }
          })
        }}>
        <Error error={error} />
        <fieldset disabled={loading} aria-busy={loading}>
        <label htmlFor="file">
              Title
            <input
              type="file"
              id="file"
              name="file"
              placeholder="Upload and Image"
              required
              value={this.state.image}
              onChange={this.uploadFile}
            />
          </label>
          <label htmlFor="title">
              Title
            <input
              type="text"
              id="title"
              name="title"
              placeholder="Title"
              required
              value={this.state.title}
              onChange={this.handleChange}
            />
          </label>

          <label htmlFor="price">
              Price
            <input
              type="number"
              id="price"
              name="price"
              placeholder="Price"
              required
              value={this.state.price}
              onChange={this.handleChange}
            />
          </label>

          <label htmlFor="title">
              Description
            <textarea
              type="text"
              id="description"
              name="description"
              placeholder="Description"
              required
              value={this.state.description}
              onChange={this.handleChange}
            />
          </label>
          <button type="submit">Submit</button>
        </fieldset>
        </Form>
      )}
    </Mutation>
    );
  }
}

export default CreateItem;
export { CREATE_ITEM_MUATION }
