import React from 'react';
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'
import { CURRENT_USER_QUERY } from './User'

const ADD_TO_CART_MUATATION = gql`
  mutation addToCart($id: ID!) {
    addToCart(id: $id) {
      id
      quantity
    }
  }
`

class AddToCart extends React.Component {
  render() {
    const { id } = this.props
    return (
    <Mutation 
      mutation={ADD_TO_CART_MUATATION} 
      variables={{
        id,
      }}
      refetchQueryies={[{ query: CURRENT_USER_QUERY }]}
    >
      {(addToCart, { loading }) => (
        <button disabled={loading} onClick={addToCart}>Add To Cart</button>
      )}
    </Mutation>
    )
  }
}

export default AddToCart;
